const $dialog=$(`<div>${[{id:"map",name:"Map",desc:"Base map layer"},{id:"floors",name:"Floors",desc:"better20 floors"},{id:"background",name:"Background",desc:"better20 background"},{id:"objects",name:"Tokens",desc:"Tokens and objects layer"},{id:"roofs",name:"Roofs",desc:"better20 roofs"},{id:"foreground",name:"Foreground",desc:"better20 foreground"},{id:"gmlayer",name:"GM",desc:"GM hidden layer"},{id:"walls",name:"DL",desc:"Dynamic Lighting walls"},{id:"weather",name:"Weather",desc:"Weather exclusions"}].reduce((e,a)=>`${e}<p><span style="display:inline-block;width:150px">${a.desc}</span><button class="btn" data-layer="${a.id}">${a.name}</button></p>`,"")}</div>`);$dialog.dialog({title:"Switch to layer",open:()=>{$dialog.on("click","button",e=>{const a=$(e.target).data("layer");currentEditingLayer=a,d20.Campaign.activePage().onLayerChange()})},close:()=>{$dialog.off(),$dialog.dialog("destroy").remove()}});